import React, { useEffect, useCallback, useState } from 'react'
import { useParams, Link } from 'react-router-dom'
import Board from '../components/board/Board'
import Button from '../components/common/Button'
import { useToast } from '../components/common/Toast'
import useBoardStore from '../store/boardStore'
import { supabase } from '../lib/supabaseClient'

const BoardPage = () => {
  const { slug } = useParams()
  const { showToast, ToastComponent } = useToast()
  const [realtimeStatus, setRealtimeStatus] = useState('DISCONNECTED')
  const [isUserInteracting, setIsUserInteracting] = useState(false)
  const { 
    currentBoard, 
    fetchBoardData, 
    isLoading, 
    error 
  } = useBoardStore()

  const handleFetchBoardData = useCallback(() => {
    if (slug) {
      fetchBoardData(slug)
    }
  }, [slug, fetchBoardData])

  useEffect(() => {
    handleFetchBoardData()
  }, [handleFetchBoardData])

  // Îã®ÏàúÌôîÎêú Ìè¨Ïª§Ïä§ Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
  useEffect(() => {
    let focusTimeout = null
    let lastFocusTime = 0

    const handleFocus = () => {
      const now = Date.now()
      if (now - lastFocusTime < 3000) return // 3Ï¥à ÎÇ¥ Ï§ëÎ≥µ Î∞©ÏßÄ

      lastFocusTime = now
      console.log('üîç Window focused - will refresh in 2 seconds')

      if (focusTimeout) clearTimeout(focusTimeout)
      focusTimeout = setTimeout(() => {
        if (currentBoard) {
          console.log('üîÑ Executing focus refresh')
          handleFetchBoardData()
        }
      }, 2000) // 2Ï¥à ÏßÄÏó∞
    }

    const handleVisibilityChange = () => {
      if (!document.hidden && currentBoard) {
        const now = Date.now()
        if (now - lastFocusTime < 3000) return

        lastFocusTime = now
        console.log('üëÅÔ∏è Page became visible - will refresh in 2 seconds')

        if (focusTimeout) clearTimeout(focusTimeout)
        focusTimeout = setTimeout(() => {
          console.log('üîÑ Executing visibility refresh')
          handleFetchBoardData()
        }, 2000) // 2Ï¥à ÏßÄÏó∞
      }
    }

    window.addEventListener('focus', handleFocus)
    document.addEventListener('visibilitychange', handleVisibilityChange)

    return () => {
      window.removeEventListener('focus', handleFocus)
      document.removeEventListener('visibilitychange', handleVisibilityChange)
      if (focusTimeout) clearTimeout(focusTimeout)
    }
  }, [currentBoard, handleFetchBoardData])

  // Ï£ºÍ∏∞Ï†Å Îç∞Ïù¥ÌÑ∞ ÏÉàÎ°úÍ≥†Ïπ® (Î∞±ÏóÖ ÎèôÍ∏∞Ìôî)
  useEffect(() => {
    if (!currentBoard) return

    const interval = setInterval(() => {
      console.log('Periodic data refresh')
      handleFetchBoardData()
    }, 30000) // 30Ï¥àÎßàÎã§ ÏÉàÎ°úÍ≥†Ïπ®

    return () => clearInterval(interval)
  }, [currentBoard, handleFetchBoardData])

  useEffect(() => {
    if (!currentBoard) return

    console.log(`Setting up realtime subscription for board: ${currentBoard.name}`)

    // Supabase Realtime Íµ¨ÎèÖ ÏÑ§Ï†ï (ÎîîÎ∞îÏö¥Ïã± Ï†ÅÏö©)
    let updateTimeout = null

    const debouncedUpdate = () => {
      if (updateTimeout) clearTimeout(updateTimeout)
      updateTimeout = setTimeout(() => {
        console.log('Realtime change detected - fetching updated board data...')
        handleFetchBoardData()
      }, 300) // 300ms ÎîîÎ∞îÏö¥Ïã±ÏúºÎ°ú Ï§ÑÏûÑ
    }

    const channel = supabase
      .channel(`board-${currentBoard.id}`)
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'columns',
          filter: `board_id=eq.${currentBoard.id}`
        },
        (payload) => {
          console.log('Column change received:', payload.eventType, payload.new || payload.old)
          debouncedUpdate()
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'cards'
        },
        (payload) => {
          console.log('Card change received:', payload.eventType, payload.new || payload.old)
          // ÌòÑÏû¨ Î≥¥ÎìúÏùò Ïª¨ÎüºÏóê ÏÜçÌïú Ïπ¥Îìú Î≥ÄÍ≤ΩÏÇ¨Ìï≠Îßå Ï≤òÎ¶¨
          const isRelevantCard = payload.new || payload.old
          if (isRelevantCard) {
            debouncedUpdate()
          }
        }
      )
      .subscribe((status) => {
        console.log('Realtime subscription status:', status)
        setRealtimeStatus(status)

        if (status === 'SUBSCRIBED') {
          showToast('Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞ÌôîÍ∞Ä ÌôúÏÑ±ÌôîÎêòÏóàÏäµÎãàÎã§', 'success', 2000)
        } else if (status === 'CHANNEL_ERROR') {
          showToast('Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞Ìôî Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§', 'error', 4000)
        }
      })

    return () => {
      console.log('Cleaning up realtime subscription')
      if (updateTimeout) clearTimeout(updateTimeout)
      supabase.removeChannel(channel)
      setRealtimeStatus('DISCONNECTED')
    }
  }, [currentBoard, handleFetchBoardData, showToast])

  // ÏóêÎü¨Í∞Ä ÏûàÏùÑ Îïå ÌÜ†Ïä§Ìä∏Î°ú ÌëúÏãúÌïòÍ≥† ÏûêÎèôÏúºÎ°ú Ïû¨ÏãúÎèÑ
  useEffect(() => {
    if (!error) return

    showToast(`Ïò§Î•ò Î∞úÏÉù: ${error}`, 'error', 5000)
    // 3Ï¥à ÌõÑ ÏûêÎèô Ïû¨ÏãúÎèÑ
    const retryTimer = setTimeout(() => {
      console.log('Auto retrying due to error...')
      handleFetchBoardData()
    }, 3000)

    return () => clearTimeout(retryTimer)
  }, [error, handleFetchBoardData, showToast])

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-lg text-gray-600">Î≥¥ÎìúÎ•º Î∂àÎü¨Ïò§Îäî Ï§ë...</div>
      </div>
    )
  }

  if (error && isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-lg text-red-600 mb-4">
            Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: {error}
          </div>
          <div className="text-sm text-gray-600 mb-4">
            3Ï¥à ÌõÑ ÏûêÎèôÏúºÎ°ú Ïû¨ÏãúÎèÑÌï©ÎãàÎã§...
          </div>
          <div className="space-x-4">
            <Button onClick={handleFetchBoardData}>
              ÏßÄÍ∏à Îã§Ïãú ÏãúÎèÑ
            </Button>
            <Link to="/">
              <Button variant="secondary">
                ÌôàÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
              </Button>
            </Link>
          </div>
        </div>
      </div>
    )
  }

  if (!currentBoard) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="text-lg text-gray-600 mb-4">
            Î≥¥ÎìúÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.
          </div>
          <Link to="/">
            <Button>ÌôàÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞</Button>
          </Link>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò Î∞î */}
      <nav className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <Link 
                to="/"
                className="text-gray-600 hover:text-gray-900 transition-colors"
              >
                ‚Üê Î≥¥Îìú Î™©Î°ù
              </Link>
              <div className="text-gray-300">|</div>
              <h1 className="text-lg font-semibold text-gray-900">
                {currentBoard.name}
              </h1>
            </div>

            <div className="flex items-center space-x-2">
              <span className="text-sm text-gray-500">
                {realtimeStatus === 'SUBSCRIBED' ? 'Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞Ìôî ÌôúÏÑ±Ìôî' : 
                 realtimeStatus === 'CONNECTING' ? 'Ïó∞Í≤∞ Ï§ë...' : 
                 realtimeStatus === 'CHANNEL_ERROR' ? 'Ïó∞Í≤∞ Ïò§Î•ò' : 'Ïó∞Í≤∞ ÎåÄÍ∏∞ Ï§ë'}
              </span>
              <div className={`w-2 h-2 rounded-full ${
                realtimeStatus === 'SUBSCRIBED' ? 'bg-green-500' : 
                realtimeStatus === 'CONNECTING' ? 'bg-yellow-500' : 
                realtimeStatus === 'CHANNEL_ERROR' ? 'bg-red-500' : 'bg-gray-400'
              }`}></div>
            </div>
          </div>
        </div>
      </nav>

      {/* Î≥¥Îìú ÏΩòÌÖêÏ∏† */}
      <Board />

      {/* Toast Ïª¥Ìè¨ÎÑåÌä∏ */}
      <ToastComponent />
    </div>
  )
}

export default BoardPage
