import React, { useEffect, useCallback, useState } from 'react'
import { useParams, Link } from 'react-router-dom'
import Board from '../components/board/Board'
import Button from '../components/common/Button'
import { useToast } from '../components/common/Toast'
import useBoardStore from '../store/boardStore'
import { supabase } from '../lib/supabaseClient'

const BoardPage = () => {
  const { slug } = useParams()
  const { showToast, ToastComponent } = useToast()
  const [realtimeStatus, setRealtimeStatus] = useState('DISCONNECTED')
  const { 
    currentBoard, 
    fetchBoardData, 
    isLoading, 
    error 
  } = useBoardStore()

  const handleFetchBoardData = useCallback(() => {
    if (slug) {
      fetchBoardData(slug)
    }
  }, [slug, fetchBoardData])

  useEffect(() => {
    handleFetchBoardData()
  }, [handleFetchBoardData])

  // Supabase Ïó∞Í≤∞ ÏÉÅÌÉú ÌôïÏù∏
  useEffect(() => {
    console.log('üîç Checking Supabase connection...')
    console.log('Supabase URL:', process.env.REACT_APP_SUPABASE_URL)
    console.log('Supabase client:', supabase)

    // Í∞ÑÎã®Ìïú Ïó∞Í≤∞ ÌÖåÏä§Ìä∏
    const testConnection = async () => {
      try {
        const { data, error } = await supabase.from('boards').select('count').limit(1)
        if (error) {
          console.error('‚ùå Supabase connection test failed:', error)
        } else {
          console.log('‚úÖ Supabase connection test successful:', data)
        }
      } catch (err) {
        console.error('‚ùå Supabase connection error:', err)
      }
    }

    testConnection()
  }, [])

  // Îã®ÏàúÌôîÎêú Ìè¨Ïª§Ïä§ Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
  useEffect(() => {
    let focusTimeout = null
    let lastFocusTime = 0

    const handleFocus = () => {
      const now = Date.now()
      if (now - lastFocusTime < 3000) return // 3Ï¥à ÎÇ¥ Ï§ëÎ≥µ Î∞©ÏßÄ

      lastFocusTime = now
      console.log('üîç Window focused - will refresh in 2 seconds')

      if (focusTimeout) clearTimeout(focusTimeout)
      focusTimeout = setTimeout(() => {
        if (currentBoard) {
          console.log('üîÑ Executing focus refresh')
          handleFetchBoardData()
        }
      }, 2000) // 2Ï¥à ÏßÄÏó∞
    }

    const handleVisibilityChange = () => {
      if (!document.hidden && currentBoard) {
        const now = Date.now()
        if (now - lastFocusTime < 3000) return

        lastFocusTime = now
        console.log('üëÅÔ∏è Page became visible - will refresh in 2 seconds')

        if (focusTimeout) clearTimeout(focusTimeout)
        focusTimeout = setTimeout(() => {
          console.log('üîÑ Executing visibility refresh')
          handleFetchBoardData()
        }, 2000) // 2Ï¥à ÏßÄÏó∞
      }
    }

    window.addEventListener('focus', handleFocus)
    document.addEventListener('visibilitychange', handleVisibilityChange)

    return () => {
      window.removeEventListener('focus', handleFocus)
      document.removeEventListener('visibilitychange', handleVisibilityChange)
      if (focusTimeout) clearTimeout(focusTimeout)
    }
  }, [currentBoard, handleFetchBoardData])

  // Ï£ºÍ∏∞Ï†Å Îç∞Ïù¥ÌÑ∞ ÏÉàÎ°úÍ≥†Ïπ® (Î∞±ÏóÖ ÎèôÍ∏∞Ìôî)
  useEffect(() => {
    if (!currentBoard) return

    const interval = setInterval(() => {
      console.log('Periodic data refresh')
      handleFetchBoardData()
    }, 30000) // 30Ï¥àÎßàÎã§ ÏÉàÎ°úÍ≥†Ïπ®

    return () => clearInterval(interval)
  }, [currentBoard, handleFetchBoardData])

  useEffect(() => {
    if (!currentBoard) return

    console.log(`üöÄ Setting up realtime subscription for board: ${currentBoard.name} (ID: ${currentBoard.id})`)

    // Supabase Realtime Íµ¨ÎèÖ ÏÑ§Ï†ï
    let updateTimeout = null

    const debouncedUpdate = (source) => {
      if (updateTimeout) clearTimeout(updateTimeout)
      updateTimeout = setTimeout(() => {
        console.log(`‚ö° ${source} change detected - fetching updated board data...`)
        handleFetchBoardData()
      }, 500) // 500ms ÎîîÎ∞îÏö¥Ïã±
    }

    const channelName = `board-${currentBoard.id}`
    console.log(`üì° Creating channel: ${channelName}`)

    const channel = supabase
      .channel(channelName)
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'columns',
          filter: `board_id=eq.${currentBoard.id}`
        },
        (payload) => {
          console.log('üìã Column change received:', {
            eventType: payload.eventType,
            table: payload.table,
            new: payload.new,
            old: payload.old
          })
          debouncedUpdate('Column')
        }
      )
      .on(
        'postgres_changes',
        {
          event: '*',
          schema: 'public',
          table: 'cards'
        },
        (payload) => {
          console.log('üÉè Card change received:', {
            eventType: payload.eventType,
            table: payload.table,
            new: payload.new,
            old: payload.old
          })
          debouncedUpdate('Card')
        }
      )
      .subscribe((status) => {
        console.log(`üì∂ Realtime subscription status: ${status}`)
        setRealtimeStatus(status)

        if (status === 'SUBSCRIBED') {
          console.log('‚úÖ Successfully subscribed to realtime updates')
          showToast('Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞ÌôîÍ∞Ä ÌôúÏÑ±ÌôîÎêòÏóàÏäµÎãàÎã§', 'success', 2000)
        } else if (status === 'CHANNEL_ERROR') {
          console.error('‚ùå Realtime subscription error')
          showToast('Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞Ìôî Ïó∞Í≤∞Ïóê Î¨∏Ï†úÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§', 'error', 4000)
        } else if (status === 'TIMED_OUT') {
          console.error('‚è∞ Realtime subscription timed out')
          showToast('Ïã§ÏãúÍ∞Ñ ÎèôÍ∏∞Ìôî Ïó∞Í≤∞ ÏãúÍ∞Ñ Ï¥àÍ≥º', 'warning', 3000)
        } else if (status === 'CLOSED') {
          console.log('üîå Realtime subscription closed')
        }
      })

    return () => {
      console.log(`üßπ Cleaning up realtime subscription for ${channelName}`)
      if (updateTimeout) clearTimeout(updateTimeout)
      supabase.removeChannel(channel)
      setRealtimeStatus('DISCONNECTED')
    }
  }, [currentBoard, handleFetchBoardData, showToast])

  // ÏóêÎü¨Í∞Ä ÏûàÏùÑ Îïå ÌÜ†Ïä§Ìä∏Î°ú ÌëúÏãúÌïòÍ≥† ÏûêÎèôÏúºÎ°ú Ïû¨ÏãúÎèÑ
  useEffect(() => {
    if (!error) return

    showToast(`Ïò§Î•ò Î∞úÏÉù: ${error}`, 'error', 5000)
    // 3Ï¥à ÌõÑ ÏûêÎèô Ïû¨ÏãúÎèÑ
    const retryTimer = setTimeout(() => {
      console.log('Auto retrying due to error...')
      handleFetchBoardData()
    }, 3000)

    return () => clearTimeout(retryTimer)
  }, [error, handleFetchBoardData, showToast])

